mapper.insert:
insert into yyzj_c_join_sys (sys_id, name, info, 
  ftp_ip, ftp_port, ftp_path, 
  username, password, valid
  )
values (#{sysId,jdbcType=CHAR}, #{name,jdbcType=VARCHAR}, #{info,jdbcType=VARCHAR}, 
  #{ftpIp,jdbcType=VARCHAR}, #{ftpPort,jdbcType=VARCHAR}, #{ftpPath,jdbcType=VARCHAR}, 
  #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{valid,jdbcType=CHAR}
  )

mapper.select:
    select 
    s.sys_id, s.name, s.info, s.ftp_ip, s.ftp_port, s.ftp_path, s.username, s.password, s.valid
    from yyzj_c_engine e, yyzj_c_engine_to_sys t, yyzj_c_join_sys s
	 where 1=1
	 and e.engine_id = t.engine_id
	 and t.sys_id = s.sys_id
	 and e.engine_ip = #{engineIp,jdbcType=VARCHAR}
	 and e.engine_port = #{enginePort,jdbcType=VARCHAR}
	 and e.type = #{type,jdbcType=VARCHAR}
	 and e.version = #{version,jdbcType=VARCHAR}
	 and s.valid = '1'
