mapper.select:
	select 

    from yyzj_c_score_total
    where 1=1 

       and total_score_id = #{totalScoreId} 


      and model_id  = #{modelId} 


      and sys_id = #{sysId} 


       and name like #{name} 


       and info = #{info} 


       and user_type = #{userType} 


       and score_value = #{scoreValue} 


       and create_user = #{createUser} 


       and create_time >= #{createTime} 


       and create_time <= #{endTime} 


       and publish_user = #{publishUser} 


       and publish_time = #{publishTime} 


       and publish_mark = #{publishMark} 


       and delete_mark = #{deleteMark} 


       and stop_time = #{stopTime} 

    order by create_time desc
