mapper.select:
select
distinct
${column.escapedColumnName}
rule_id, analyzer_id, `name`, info, context, create_user, create_time, publish_user, publish_time, publish_mark, delete_mark, `type`, rule_label, context_show, builtin_mark, result_type
from yyzj_c_rule
order by ${example.orderByClause}

select
${column.escapedColumnName}
rule_id, analyzer_id, `name`, info, context, create_user, create_time, publish_user, publish_time, publish_mark, delete_mark, `type`, rule_label, context_show, builtin_mark, result_type
from yyzj_c_rule where rule_id = #{ruleId,jdbcType=CHAR}

select
${column.escapedColumnName}
rule_id, analyzer_id, `name`, info, context, create_user, create_time, publish_user, publish_time, publish_mark, delete_mark, `type`, rule_label, context_show, builtin_mark, result_type
from yyzj_c_rule
order by ${example.orderByClause}
limit 1

select   from
(select

from yyzj_c_rule
where 1=1

  and rule_id = #{ruleId}


  and analyzer_id = #{analyzerId}


  and name like #{name}


  and info = #{info}


  and  context = #{context}


  and create_user = #{createUser}


  and create_time = #{createTime}


  and  publish_user = #{publishUser}


  and  publish_time = #{publishTime}


  and publish_mark = #{publishMark}


  and delete_mark = #{deleteMark}


  and type = #{type}


  and context_show like #{contextShow}


  and builtin_mark = #{builtinMark}


  and result_type = #{resultType}


  and rule_label = #{ruleLabel}
) r
left join (select  from yyzj_d_sys_dict
where parent_id=#{dictParentId}) d on r.rule_label = d.value
order by create_time desc

select count(*) from
(select

from yyzj_c_rule
where 1=1

  and rule_id = #{ruleId}


  and analyzer_id = #{analyzerId}


  and name like #{name}


  and info = #{info}


  and  context = #{context}


  and create_user = #{createUser}


  and create_time = #{createTime}


  and  publish_user = #{publishUser}


  and  publish_time = #{publishTime}


  and publish_mark = #{publishMark}


  and delete_mark = #{deleteMark}


  and type = #{type}


  and context_show like #{contextShow}


  and builtin_mark = #{builtinMark}


  and result_type = #{resultType}


  and rule_label = #{ruleLabel}
) r
left join (select  from yyzj_d_sys_dict
where parent_id=#{dictParentId}) d on r.rule_label = d.value
